import React, { useState } from "react";
import "./Todo.css";

export default function Todo(props) {
  // mba state nese eshte editable
  const [isEditable, setIsEditable] = useState(false);

  // shto llogjike kur ti besh edit
  function handleEdit() {
    setIsEditable(true);
  }
  // cancel edit
  // submit edit
  return (
    <div className="todo-element row">
      <div className="col-6">
        <h3>{props.todo}</h3>
      </div>
      <div className="col-3">
        <button className="btn btn-info btn-sm" onClick={props.handleEdit}>
          Edit
        </button>
      </div>
      <div className="col-3">
        <button
          className="btn btn-danger btn-sm"
          onClick={() => props.handleDelete(props.todo)}
        >
          Delete
        </button>
      </div>
    </div>
  );
}
