import React, { useState } from "react";
import "./Todo.css";

export default function Todo(props) {
  const [isEditable, setIsEditable] = useState(false);

  function handleEdit() {
    setIsEditable(true);
  }

  if (isEditable) {
    return (
      <div className="todo-element row">
        <div className="col-6">
          <input type="text" value={props.todo} />
        </div>
        <div className="col-3">
          <button className="btn btn-info btn-sm" onClick={props.handleEdit}>
            Submit Edit
          </button>
        </div>
        <div className="col-3">
          <button
            className="btn btn-danger btn-sm"
            onClick={() => props.handleDelete(props.todo)}
          >
            Delete
          </button>
        </div>
      </div>
    );
  } else {
    return (
      <div className="todo-element row">
        <div className="col-6">
          <h3>{props.todo}</h3>
        </div>
        <div className="col-3">
          <button className="btn btn-info btn-sm" onClick={handleEdit}>
            Edit
          </button>
        </div>
        <div className="col-3">
          <button
            className="btn btn-danger btn-sm"
            onClick={() => props.handleDelete(props.todo)}
          >
            Delete
          </button>
        </div>{" "}
      </div>
    );
  }
}
